mapper.select:
SELECT * FROM ( SELECT US.RESOURCES_ID, US.BRAND, US.MODEL, US.PRICE, US.LAST_PRICE, US.MATERIAL, US.ADDRESS, US.FREIGHT, US.YN_FLAG, US.CREATED_TIME, US.MODIFIED_TIME, US.SUBORDINATE_AREA FROM lot_resources US WHERE 1=1
and US.YN_FLAG = #{ynFlag}
and (US.BRAND like concat('%',#{brand},'%') or US.ADDRESS like concat('%',#{brand},'%'))
and (US.MODEL like concat('%',#{material_or_model},'%') or US.MATERIAL like concat('%',#{material_or_model},'%'))
and US.CREATED_TIME >=#{createTimeStart} and US.CREATED_TIME <=#{createTimeEnd}
and US.CREATED_TIME >=#{startSelectDate} and US.CREATED_TIME <=#{endSelectDate}
)RESULT WHERE 1=1
ORDER BY ${orderColumn} ${orderDirection}
ORDER BY RESULT.CREATED_TIME ASC
limit #{index}, #{pageSize}

SELECT * FROM ( SELECT count(*) FROM lot_resources US WHERE 1=1
and US.YN_FLAG = #{ynFlag}
and (US.BRAND like concat('%',#{brand},'%') or US.ADDRESS like concat('%',#{brand},'%'))
and (US.MODEL like concat('%',#{material_or_model},'%') or US.MATERIAL like concat('%',#{material_or_model},'%'))
and US.CREATED_TIME >=#{createTimeStart} and US.CREATED_TIME <=#{createTimeEnd}
)RESULT

SELECT US.RESOURCES_ID, US.BRAND, US.MODEL, US.PRICE, US.LAST_PRICE, US.MATERIAL, US.ADDRESS, US.FREIGHT, US.YN_FLAG, US.CREATED_TIME, US.MODIFIED_TIME, US.SUBORDINATE_AREA FROM lot_resources US WHERE US.YN_FLAG = 'Y' and US.SUBORDINATE_AREA like concat('%',#{address},'%') and US.MATERIAL = #{material}
and US.BRAND in
#{item}
and US.MODEL like concat('%',#{model},'%') order by US.PRICE ASC

select US.resources_history_id as resourcesHistoryId, US.resources_id as resourcesId, US.address, US.brand , US.material , US.model , US.price, US.last_Price as lastPrice, US.CREATED_TIME as createdTime, US.subordinate_area as subordinateArea from lot_resources_history US where 1=1
and US.CREATED_TIME >=#{startDate} and US.CREATED_TIME <=#{endDate}
and US.MODEL = 'HRB400E Ï†18'
and US.brand = #{brand}
and US.subordinate_area like concat('%',#{address},'%')
order by US.created_time desc
